sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {

let currentQuery = api.state.listQuery;
let payloadQuery = event.payload.heListQuery;
let newQuery = "";


if(payloadQuery.includes("=")) {

   if(currentQuery.includes("^u_request_type=")) {
     newQuery = payloadQuery + "^u_request_type=" + currentQuery.split("^u_request_type=")[1];
   } 
   else {
     newQuery = payloadQuery;
   }

}
else {

   if(currentQuery.includes("^u_request_type=")) {
     newQuery = currentQuery.split("^u_request_type=")[0] + "^u_request_type=" + payloadQuery;
   } 
   else {
     newQuery = currentQuery + "^u_request_type=" + payloadQuery;
   }

}
 api.setState('listTitle', newQuery);
 api.setState('listQuery', newQuery);

}
